import React, {useState} from "react";
import {Avatar, Checkbox, Dropdown, Menu} from "antd";

import labels from "../../../../../routes/mail/data/labels";
import AddMail from './../../addMail'


const options = [
  'Delete',
];
const MailListItem = ({mail, onMailSelect, onMailChecked, onStartSelect}) => {
  const [contactState, setContactState] = useState(false)
  const menus = () => (<Menu onClick={(e) => {
    if (e.key === 'Edit') {
      this.onEditContact()
    } else {
      this.onDeleteContact(this.props.contact)
    }
  }
  }>
    {options.map(option =>
      <Menu.Item key={option}>
        {option}
      </Menu.Item>,
    )}
  </Menu>);
  return (
    <div className="gx-module-list-item gx-mail-cell">

      <div className="gx-mail-list-info" onClick={() => {
        onMailSelect(mail);
      }}>

        <div className="gx-module-list-content">
          <div className="gx-mail-user-des">

            <span className="gx-sender-name">{mail.sender.name}</span>

            <span className="gx-toolbar-separator">&nbsp;</span>

            <span className="gx-d-inline-block gx-text-truncate gx-send-subject">{mail.title}</span>

          
            <div className="gx-time">{mail.creation_date}</div>

          </div>


          <div className="gx-message">
            <p className="gx-text-truncate"> {mail.description}</p>

          </div>
          <div className="gx-module-contact-right">

            <Dropdown overlay={this.menus()} placement="bottomRight" trigger={['click']}>
              <i className="gx-icon-btn icon icon-ellipse-v"/>
            </Dropdown>
{/* 
            {contactState &&
            <AddMail open={addContactState} contact={contact} onSaveContact={onSaveContact}
                        onContactClose={this.onContactClose} onDeleteContact={this.onDeleteContact}/>} */}
            </div>
        </div>

      </div>

    </div>
  )
};

export default MailListItem;
