import React from "react";
import {Avatar, Input, Modal, Checkbox} from "antd";

import IntlMessages from "../../../../util/IntlMessages";

class AddContact extends React.Component {
  constructor(props) {
    super(props);
    const TextArea = {Input}
    const {id, collect, description, img, royalty, title, pdf} = props.contact;
    this.state = {
      id,
      title,
      collect,
      description,
      verified: false,
      img,
      pdf,
      royalty,
    }
  }

  render() {
    const {onSaveContact, onContactClose, open, contact} = this.props;
    const {collect, description, royalty, img, pdf, verified, title} = this.state
    
    return (
      <Modal
        title={contact.description === '' ?
          <IntlMessages id="contact.addContact"/> :
          <IntlMessages id="contact.saveContact"/>}
        toggle={onContactClose} visible={open}
        closable={false}
        onOk={() => {
          if (name === '')
            return;
          onContactClose();
          onSaveContact(
            {
              collect, description, royalty, img
            });
          this.setState({
            
            'collect': '',
            'description': '',
            'img': '',
            'royalty': 0,
          })

        }}
        onCancel={onContactClose}>

        <div className="gx-modal-box-row">

          <div className="gx-modal-box-form-item">
            {img ?           <div className="gx-form-group">
              <img src={img}/>
            </div> : null}

            <div className="gx-form-group">
              <Input
                required
                placeholder="title"
                onChange={(event) => this.setState({title: event.target.value})}
                defaultValue={title}
                margin="none"/>
            </div>
            <div className="gx-form-group">
              <Input
                required
                placeholder="collection"
                onChange={(event) => this.setState({collect: event.target.value})}
                defaultValue={collect}
                margin="none"/>
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="description"
                onChange={(event) => this.setState({description: event.target.value})}
                value={description}
                margin="normal"/>
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="Wallet"
                type='number'
                onChange={(event) => this.setState({royalty: event.target.value})}
                value={royalty}
                margin="normal"
              />
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="img"
                type='file'
                onChange={(event) => this.setState({img: event.target.value})}
                margin="normal"
              />
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="pdf"
                type='file'
                onChange={(event) => this.setState({pdf: event.target.value})}
                margin="normal"
              />
            </div>
            <div className="gx-form-group">
              
              <Checkbox
                placeholder="verified"
                type='checkbox'
                onChange={(event) => this.setState({verified: event.target.checked})}
                margin="normal"
              >Verified</Checkbox>
            </div>
          </div>
        </div>
      </Modal>
    );
  }
}

export default AddContact;
