import React from "react";
import {Avatar, Input, Modal} from "antd";

import IntlMessages from "../../../../util/IntlMessages";

class AddMail extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      subtitle: '',
      text: '',
      imgUrl: '',
      url: ''
    }
  }


  render() {
    const {onSaveMail, onMailClose, open, currentMail} = this.props;
    const {title, subtitle, text, imgUrl, url} = this.state;
    return (
      <Modal
        title={
          <IntlMessages id="contact.addContact"/>}
        toggle={onMailClose} visible={open}
        closable={false}
        onOk={() => {
          if (name === '')
            return;
          onMailClose();
          onSaveMail({
              'title': title,
              'text': text,
              'url': url,
              'imgUrl': imgUrl
          });
          this.setState()

        }}
        onCancel={onMailClose}>

        <div className="gx-modal-box-row">

          <div className="gx-modal-box-form-item">
            <div className="gx-form-group">
              <Input
                required
                placeholder="title"
                onChange={(event) => this.setState({title: event.target.value})}
                defaultValue={title}
                margin="none"/>
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="subtitle"
                onChange={(event) => this.setState({subtitle: event.target.value})}
                value={subtitle}
                margin="normal"/>
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="text"
                onChange={(event) => this.setState({text: event.target.value})}
                value={text}
                margin="normal"
              />
            </div>
            <div className="gx-form-group">
              <Input
                placeholder="url"
                onChange={(event) => this.setState({url: event.target.value})}
                value={url}
                margin="normal"
              />
            </div>
            <div className="gx-form-group">
              <Input
                type='image'
                placeholder="image"
                onChange={(event) => this.setState({imgUrl: event.target.value})}
                value={imgUrl}
                margin="normal"
              />
            </div>
          </div>
        </div>
      </Modal>
    );
  }
}

export default AddMail;
